class Solution(object):    def removeDuplicates(self, nums):        """        :type nums: List[int]        :rtype: int        """        val_to_comp = nums[0]        repet = 1        ret=1        for idx in range(0, len(nums) - 1, 1):            if(val_to_comp == nums[idx+1]):                repet += 1                if(repet>2):                    nums[idx+1] = 101                else:                    ret += 1                continue            else:                ret += 1                val_to_comp = nums[idx+1]                repet = 1            # print(nums)        nums.sort()        # print(nums)        return rets=Solution()val = s.removeDuplicates([1,1,1,2,2,3])print(val)